import React, { Component } from 'react'

import { CardList } from './components/card-list/card-list.component'

import { SearchBox } from './components/search-box/search-box.component'

import './App.css'

class App extends Component {
  constructor () {
    super()

    this.state = {
      monsters: [],
      searchField: ''
    };

    //this.handleChange = this.handleChange.bind(this);
    //.bind 

  }

  componentDidMount () {
    //we are using comDidMount because we have access to it
    // we are fetching and taking a response
    //we are taking users that we got from it and are giving it to monsters array
    //this fetch return a promise
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json())
      .then(users => this.setState({ monsters: users }))
  }

  handleChange = (e) => {
    this.setState({ searchField: e.target.value })
  }

  render () {
    //.filter method and .includes for search

    //this is destructuring
    const { monsters, searchField } = this.state
    const filteredMonsters = monsters.filter(monster =>
      monster.name.toLowerCase().includes(searchField.toLowerCase())
    )
    return (
      <div className='App'>
        <h1>Monsters Rolodex</h1>
        <SearchBox
          placeholder='search monsters'
          //this is a pecial keyword in javascript
          //this gives us the reference
          //this keyword is set to our context
          //this keywords gets bound differently based on out class methods
          handleChange={this.handleChange}
        />
        <CardList monsters={filteredMonsters} />
      </div>
    )
  }
}

export default App
