import React, { Component } from 'react'
import './App.css'

class App extends Component {
  constructor () {
    super();

    this.state = {
      monsters: []
    }
  }

  componentDidMount() {
    //we are using comDidiMount because we have access to it
    // we are fetching and taking a response 
    //we are taking users that we got from it and are giving it to monsters array
    //this fetch return a promise
    fetch('https://jsonplaceholder.typicode.com/users') 
    .then(response => response.json())
    .then(users => this.setState( {monsters: users}));
  }

  render () {
    return (
      <div className='App'>
        {this.state.monsters.map(monster => (
          <h1 key={monster.id}>{monster.name}</h1>
        ))}
      </div>
    )
  }
}

export default App
